function updateSiteTheme(n,t){for(var r=document.getElementsByClassName("theme-picker"),i=0;i<r.length;i++)r[i].classList.remove("active");t.parentNode.classList.add("active");try{document.getElementById("CssTheme").href="Content/"+n+".min.css"}catch(u){document.getElementById("CssTheme").href="Content/Retro.min.css";console.log("Exception at updateSiteTheme() "+u.message)}}function currentSlide(n){showSlides(slideIndex=n)}function showSlides(n){var t,i=document.getElementsByClassName("mySlides"),r=document.getElementsByClassName("dot");for(n>i.length&&(slideIndex=1),n<1&&(slideIndex=i.length),t=0;t<i.length;t++)i[t].style.display="none";for(t=0;t<r.length;t++)r[t].className=r[t].className.replace(" active","");i[slideIndex-1].style.display="block";r[slideIndex-1].className+=" active"}function getLowercase(n){try{return n.toLowerCase()}catch(t){return console.log("Exception at getLowercase() "+t.message),n}}var slideIndex=0;showSlides(slideIndex=1);